                        break;
                    //backward arrow
                    case 37:
                        if ( $('.hp-slideshow-dimmer').is(':visible')) {
                            eventController.open_overlay_dimmer();
                            eventController.open_fly_out();
                            eventController.prev_slide();
                            hp_ss_track.trigger_tracking({ type: 'keypress', target: 'back'});
